(function($) {

//Activate the wow_plugin
  wow = new WOW(
  { 
  mobile: false //disable wow_plugin on mobile devices
  }
  )
  wow.init();

  //Div that follows down screen
  $(document).ready(function() {
  var current_top = parseInt($('#follow').css('top'));
	$('#terms').click(function() {
    $('#follow').fadeIn(1000);
	});
	$('#btn').click(function() {
	  $('#follow').fadeOut(500);
	});
      $(window).scroll(function() {
	  var top = $(window).scrollTop();
	  $('#follow').css('top', top + current_top);
	  });
  });
	
  
  // Smooth scrolling using jQuery easing
  $('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
    if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
      if (target.length) {
        $('html, body').animate({
          scrollTop: (target.offset().top - 48)
        }, 1000, "easeInOutExpo");
        return false;
      }
    }
  });

  // Closes responsive menu when a scroll trigger link is clicked
  $('.js-scroll-trigger').click(function() {
    $('.navbar-collapse').collapse('hide');
  });

  // Collapse the navbar when page is scrolled
  $(window).scroll(function() {
    if ($("#mainNav").offset().top > 100) {
      $("#mainNav").addClass("navbar-shrink");
    } else {
      $("#mainNav").removeClass("navbar-shrink");
    }
  });
	
	//call SlideNotice
	$('#save').click(function() {
	function slideNotice(text) {
	$('#slideNotice').html('<h4>' + text + '</h4>').slideDown().delay(2000).slideUp();
	}
	slideNotice('You can also advertise with us. Just email us.');
	});

  
})(jQuery);